import { Box, Button, FormControl, InputBase, makeStyles, TextareaAutosize } from "@material-ui/core";
import { AddCircle } from "@material-ui/icons"
import { useState , useEffect } from "react";
import { useHistory } from 'react-router-dom';

import {createPost, uploadFile } from '../../service/api'

const useStyles = makeStyles((theme) => ({
    container: {
        padding: '0 100px',
        [theme.breakpoints.down('md')]: {
            padding: 0
        }
    },
    image: {
        width: '100%',
        height: '50vh',
        objectFit: 'cover'
    },
    form:{
        display:'flex',
        flexDirection:'row',
        marginTop: 10,

    },
    textField:{
        flex:1,
        margin:'0 30px',
        fontSize: 25
    },
    textarea:{
        width:'100%',
        marginTop:50,
        border:'none',
        fontSize:18,
        '&:focus-visible':{
            outline:'none'
        }
    }
}
));

const intialValue={
    title:'',
    description:'',
    picture:'',
    username:'shivansh shukla',
    categories:'All',
    createDate:new Date()
}


const CreateView = () => {
    const classes = useStyles();
    const url = 'https://images.unsplash.com/photo-1543128639-4cb7e6eeef1b?ixid=MnwxMjA3fDB8MHxzZWFyY2h8Mnx8bGFwdG9wJTIwc2V0dXB8ZW58MHx8MHx8&ixlib=rb-1.2.1&w=1000&q=80';
    let history=useHistory();

    const [post,setPost]=useState(intialValue);

    const [file,setFile] = useState('');
    

    useEffect(() =>{
        const getImage= async () =>{
            consle.log(file);
            if(file){
                const data= new FormData();
                data.append("name",file.name);
                data.append('file',file);

                await uploadFile(data);
            }


        }
        getImage();
    }, [file])
    const handleChange = (e) => {
        setPost({ ...post,[e.target.name]:e.target.value })
    }

    const savePost = async () =>{
        await createPost(post);
        history.push('/');
    }
    return (
        <Box className={classes.container}>
            
            <img className={classes.image} src={url} alt='createView img' />

            <FormControl className={classes.form}>
                <label htmlFor='FileInput'>
                    <AddCircle 
                        fontSize='large' 
                        color='action' 
                    />
                </label>
                <input 
                    type='file'
                    onChange={(e) => setFile(e.target.files[0])}
                    id='FileInput'
                    style={{display:'none'}}/>
               
                <InputBase 
                    onChange={(e) => handleChange(e)} 
                    placeholder="Title" 
                    className={classes.textField}
                    name='title'
                />

                <Button 
                    onClick={() => savePost()} 
                    variant='contained' 
                    color='primary'>Publish</Button>

            </FormControl>
            
            <TextareaAutosize 
                onChange={(e) => handleChange(e)} 
                className={classes.textarea} 
                name="description"
                rowsMin='5' 
                placeholder='Write your content here.........'
            />

        </Box>
    )
}
export default CreateView;
