                ...comment,
                name: post.username,
                postId: post._id,
                comments: e.target.value
            });
            setData(e.target.value);
        }

        const addComment = async () => {
            await newComment(comment);
            setData('')
            setToggle(prev => !prev);
        }


    console.log(post);
