import { Box, makeStyles, Typography } from '@material-ui/core';
import { Edit, Delete } from '@material-ui/icons';
import { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import { getPost } from '../../service/api.js';

const useStyles = makeStyles((theme)=>({
    container: {
        padding: '0 100px',
        [theme.breakpoints.down('md')]:{
            padding:0
        }
    },
    image: {
        width: '100%',
        height: '50vh',
        objectFit: 'cover'

    },
    icons: {
        float: 'right',
    },
    icon: {
        margin: 7,
        border: '1px solid #878787',
        pading: 5,
        borderRadius: 10,
    },
    heading: {
        fontSize: 38,
        fontWeight: 600,
        textAlign: 'center',
        margin: '50px 0 10px 0'
    },
    subheading: {
        color: '#878787',
        display: 'flex',
        margin: '20px 0',
        [theme.breakpoints.down('sm')]:{
            display:'block'
        }
    }
}))
const DetailView = ({match}) => {
    const classes = useStyles();
    const url = 'https://images.unsplash.com/photo-1543128639-4cb7e6eeef1b?ixid=MnwxMjA3fDB8MHxzZWFyY2h8Mnx8bGFwdG9wJTIwc2V0dXB8ZW58MHx8MHx8&ixlib=rb-1.2.1&w=1000&q=80';
    
    const [post,setPost] = useState({});

    useEffect(() => {
        const fetchData = async () =>{
            let data=await getPost(match.params.id);
            console.log(data);
            setPost(data);
        }
        fetchData();
    },[])
    return (
        <Box className={classes.container}>
            <img src={url} alt='banner' className={classes.image} />
            
            <Box className={classes.icons}>
                <Link to='/update'>
                    <Edit 
                        className={classes.icon} 
                        color='primary' 
                        fontSize='large'/>
                </Link>
                <Delete 
                    className={classes.icon} 
                    color='error' 
                    fontSize='large' />
            </Box>
            
            <Typography className={classes.heading}>Title of the Blog</Typography>
            
            <Box className={classes.subheading}>
                <Typography>Author: <span style={{ fontWeight: 700 }}>Shivansh Shukla</span></Typography>
                <Typography style={{ marginLeft: 'auto' }}>11 Aug 2021</Typography>
            </Box>
            
            <Typography>This is a Blog.This is a Blog.This is a Blog.This is a Blog.This is a Blog.This is a Blog.This is a Blog.This is a Blog.This is a Blog.This is a Blog.This is a Blog.This is a Blog.This is a Blog.This is a Blog.This is a Blog.This is a Blog.This is a Blog.This is a Blog.This is a Blog.This is a Blog.This is a Blog.This is a Blog.This is a Blog.</Typography>
        </Box>
    )
}

export default DetailView;
