            </Route>
          <Route exact path='/create' component={CreatePost} />
          <Route exact path='/update/:id' component={Update} />
          <Route exact path='/about' component={About} />
          <Route exact path='/contact' component={Contact} />
        </Switch>
      </Box>
    </BrowserRouter>
  );
}
