import { Box, Button, FormControl, InputBase, makeStyles } from "@material-ui/core";
import { AddCircle } from "@material-ui/icons"

const useStyles = makeStyles((theme) => ({
    container: {
        padding: '0 100px',
        [theme.breakpoints.down('md')]: {
            padding: 0
        }
    },
    image: {
        width: '100%',
        height: '50vh',
        objectFit: 'cover'
    },
    form:{
        display:'flex',
        flexDirection:'row',
        marginTop: 10,

    },
    textField:{
        flex:1,
        margin:'0 30px',
        fontSize: 25
    }
}));


const CreateView = () => {
    const classes = useStyles();
    const url = 'https://images.unsplash.com/photo-1543128639-4cb7e6eeef1b?ixid=MnwxMjA3fDB8MHxzZWFyY2h8Mnx8bGFwdG9wJTIwc2V0dXB8ZW58MHx8MHx8&ixlib=rb-1.2.1&w=1000&q=80';
    return (
        <Box className={classes.container}>
            <img className={classes.image} src={url} alt='createView img' />
            <FormControl className={classes.form}>
                <AddCircle fontSize='large' color='action' />
                <InputBase placeholder="Title" className={classes.textField} />
                <Button variant='content' color='primary'>Publish</Button>
            </FormControl>
        </Box>
    )
}
export default CreateView;
