import { Box, Button, FormControl, InputBase, makeStyles, TextareaAutosize } from "@material-ui/core";
import { AddCircle } from "@material-ui/icons"
import { useEffect, useState } from "react";
import { getPost,updatePost } from "../../service/api.js";
import { useHistory } from "react-router-dom";

const useStyles = makeStyles((theme) => ({
    container: {
        padding: '0 100px',
        [theme.breakpoints.down('md')]: {
            padding: 0
        }
    },
    image: {
        width: '100%',
        height: '50vh',
        objectFit: 'cover'
    },
    form:{
        display:'flex',
        flexDirection:'row',
        marginTop: 10,

    },
    textField:{
        flex:1,
        margin:'0 30px',
        fontSize: 25
    },
    textarea:{
        width:'100%',
        marginTop:50,
        border:'none',
        fontSize:18,
        '&:focus-visible':{
            outline:'none'
        }
    }
}
));


const intialValue={
    title:'',
    description:'',
    picture:'',
    username:'shivansh shukla',
    categories:'All',
    createDate:new Date()
}


const UpdateView = ({match}) => {
    const classes = useStyles();
    const url = 'https://images.unsplash.com/photo-1543128639-4cb7e6eeef1b?ixid=MnwxMjA3fDB8MHxzZWFyY2h8Mnx8bGFwdG9wJTIwc2V0dXB8ZW58MHx8MHx8&ixlib=rb-1.2.1&w=1000&q=80';
    const history=useHistory();
    const [post,setPost] = useState(intialValue);

    useEffect(() => {
        const fetchData = async() =>{
            let data =await getPost(match.params.id);
            console.log(data);
            setPost(data);
        }
        fetchData();
    },[]);

    const handleChange = (e) => {
        setPost({ ...post,[e.target.name]:e.target.value });
    }

    const updateBlog = async() =>{
        await updatePost(match.params.id, post);
        history.Push(`/details/${match.params.id}`);
    }


    return (
        <Box className={classes.container}>
            <img className={classes.image} src={url} alt='createView img' />
            
            <FormControl className={classes.form}>
                <AddCircle 
                    fontSize='large' 
                    color='action' />

                <InputBase 
                    placeholder="Title" 
                    onChange={(e) => handleChange(e)}
                    className={classes.textField} 
                    value={post.title}
                    name='title'/>

                <Button 
                    onClick={() => updateBlog()}
                    variant='contained' 
                    color='primary'>UPDATE</Button>

            </FormControl>
            
            <TextareaAutosize  
                className={classes.textarea} 
                rowsMin='5'
                onChange={(e) => handleChange(e)} 
                placeholder='Write your content here.........'
                name='description'
                value={post.description}/>

        </Box>
    )
}
export default UpdateView;
