import { Box, makeStyles, TextareaAutosize,Button } from "@material-ui/core"
import { useState , useEffect} from "react";
import { newComment , getComments} from "../../service/api";

import Comment from "./Comment";

const useStyles =makeStyles({
    components:{
        marginTop:100,
        display:'flex'
    },
    image:{
        width:50,
        height:50,
        borderRadius:'50%'
    },
    textarea:{
        width:'100%',
        margin:'0 20px'
    },
    button:{
        height:40
    }
})

const intialValue={
    name:'',
    postId:'',
    date:new Date(),
    Comments:''
}
const Comments = ({post}) =>{
    const classes=useStyles();
    const url='https://static.thenounproject.com/png/12017-200.png';
    
    const [comment,setComment] = useState(intialValue);
    const [comments,setComments]=useState([]);
    useEffect(() =>{
        const getData = async () =>{
            let response=await getComments(post._id);
            setComments(response);
        }
        getData();
    },[post])
    const handleChange=(e) =>{
        setComment({
            ...comment,
            name:post.username,
            postId:post._id,
            comments:e.target.value
        })
    }

    const postComment = async () =>{
        await newComment(comment);
    }
    return(
        <Box>
            <Box className={classes.components}>
                <img src={url} alt='dp' className={classes.image}/>
                <TextareaAutosize 
                    className={classes.textarea}
                    rowsMin={5}
                    onChange={(e) => handleChange(e)}/>
                <Button
                    variant='contained'
                    color='primary'
                    size='medium'
                    className={classes.button}
                    onClick={() => postComment()}
                    >Post</Button>
            </Box>
            {
                comments && comments.map(Comment =>(
                    <Comment/>
                ))
            }
        </Box>
    )
}
export default Comments;
