import axios from 'axios';

const URL='http://localhost:8000'

export const createPost = async (post) =>{
    try{
        await axios.post(`${URL}/create`,post)
    }
     catch(error){
         console.log('Error while calling createPost API', error);
     }
}

export const getAllPosts = async(param) =>{
    try {
        let response= await axios.get(`${URL}/posts${param}`);
        return response.data;
    } catch (error) {
        console.log('Error while calling getAllposts API', error);
        
    }
}

export const getPost = async(id) =>{
    try{
        let response = await axios.get(`${URL}/post/${id}`);
        return response.data;
    } catch(error){
        console.log('Error while calling getPost api', error);
    }
}

export const updatePost = async (id, post) =>{
    try{
        await axios.post(`${URL}/update/${id}`,post);
    } catch(error){
        console.log('Error while calling updatepost api  ', error);
    }
}

export const deletePost = async(id) =>{
    try{
        await axios.delete(`${URL}/delete/${id}`);
    } catch(error){
        console.log('Error while calling the deletePost api', error);
    }
}

export const uploadFile = async (data) =>{
    try{
        return await axios.post(`${URL}/file/upload`, data)
    } catch(error){
        console.log('Error while uploading the image ', error);
    }
}
  
export const newComment = async (data) =>{
    try{
        return await axios.post(`${URL}/comment/new`,data)

    }catch(error){
        console.log('Error while calling the comment api',error);
    }
}
