import { Box, makeStyles, TextareaAutosize,Button } from "@material-ui/core"
import { useState } from "react";

const useStyles =makeStyles({
    components:{
        marginTop:100,
        display:'flex'
    },
    image:{
        width:50,
        height:50,
        borderRadius:'50%'
    },
    textarea:{
        width:'100%',
        margin:'0 20px'
    },
    button:{
        height:40
    }
})

const intialValue={
    name:'',
    postId:'',
    date:new Date(),
    Comments:''
}
const Comments = ({post}) =>{
    const classes=useStyles();
    const url='https://static.thenounproject.com/png/12017-200.png';
    
    const [comment,setComment] = useState(intialValue);

    const handleChange=(e) =>{
        setComment({
            ...comment,
            name:post.username,
            postId:post._id,
            comments:e.target.value
        })
    }


    return(
        <Box>
            <Box className={classes.components}>
                <img src={url} alt='dp' className={classes.image}/>
                <TextareaAutosize 
                    className={classes.textarea}
                    rowsMin={5}
                    onChange={(e) => handleChange(e)}/>
                <Button
                    variant='contained'
                    color='primary'
                    size='medium'
                    className={classes.button}
                    >Post</Button>
            </Box>
        </Box>
    )
}
export default Comments;
